import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router, RouterLink, ActivatedRoute } from '@angular/router';
import { CourseService } from '../../services/course.service';
import { CourseFormData } from '../../models/course.model';

@Component({
  selector: 'app-course-form',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterLink],
  templateUrl: './course-form.component.html',
  styleUrls: ['./course-form.component.css']
})
export class CourseFormComponent implements OnInit {
  course: CourseFormData = {
    courseCode: '',
    courseName: '',
    description: '',
    credits: 3,
    price: 0,
    isActive: true
  };
  
  isEditMode = false;
  courseId: number | null = null;
  
  constructor(
    private courseService: CourseService,
    private router: Router,
    private route: ActivatedRoute
  ) {}
  
  ngOnInit(): void {
    this.route.params.subscribe(params => {
      if (params['id']) {
        this.isEditMode = true;
        this.courseId = +params['id'];
        this.loadCourse(this.courseId);
      }
    });
  }
  
  loadCourse(id: number): void {
    this.courseService.getCourse(id).subscribe(course => {
      this.course = {
        courseCode: course.courseCode,
        courseName: course.courseName,
        description: course.description || '',
        credits: course.credits,
        price: course.price,
        isActive: course.isActive
      };
    });
  }
  
  onSubmit(): void {
    if (this.isFormValid()) {
      if (this.isEditMode && this.courseId) {
        this.courseService.updateCourse(this.courseId, this.course).subscribe(() => {
          this.router.navigate(['/courses']);
        });
      } else {
        this.courseService.createCourse(this.course).subscribe(() => {
          this.router.navigate(['/courses']);
        });
      }
    }
  }
  
  isFormValid(): boolean {
    return !!(
      this.course.courseCode?.trim() &&
      this.course.courseName?.trim() &&
      this.course.credits &&
      this.course.credits >= 1 &&
      this.course.credits <= 6 &&
      this.course.price &&
      this.course.price > 0
    );
  }
}
<div class="course-form-container">
  <div class="header">
    <h2>{{ isEditMode ? 'Edit Course' : 'Add New Course' }}</h2>
    <a routerLink="/courses" class="back-btn">← Back to Courses</a>
  </div>
  
  <form (ngSubmit)="onSubmit()" class="course-form">
    <div class="form-group">
      <label for="courseCode">Course Code *</label>
      <input 
        type="text" 
        id="courseCode" 
        name="courseCode"
        [(ngModel)]="course.courseCode"
        required
        minlength="3"
        maxlength="10"
        class="form-input">
    </div>
    
    <div class="form-group">
      <label for="courseName">Course Name *</label>
      <input 
        type="text" 
        id="courseName" 
        name="courseName"
        [(ngModel)]="course.courseName"
        required
        minlength="5"
        maxlength="100"
        class="form-input">
    </div>
    
    <div class="form-group">
      <label for="description">Description</label>
      <textarea 
        id="description" 
        name="description"
        [(ngModel)]="course.description"
        maxlength="500"
        rows="3"
        class="form-input"></textarea>
    </div>
    
    <div class="form-row">
      <div class="form-group">
        <label for="credits">Credits *</label>
        <input 
          type="number" 
          id="credits" 
          name="credits"
          [(ngModel)]="course.credits"
          required
          min="1"
          max="6"
          class="form-input">
      </div>
      
      <div class="form-group">
        <label for="price">Price *</label>
        <input 
          type="number" 
          id="price" 
          name="price"
          [(ngModel)]="course.price"
          required
          min="0.01"
          step="0.01"
          class="form-input">
      </div>
    </div>
    
    <div class="form-row">
      <div class="form-group">
        <label for="startDate">Start Date</label>
        <input 
          type="date" 
          id="startDate" 
          name="startDate"
          [(ngModel)]="course.startDate"
          class="form-input">
      </div>
      
      <div class="form-group">
        <label for="endDate">End Date</label>
        <input 
          type="date" 
          id="endDate" 
          name="endDate"
          [(ngModel)]="course.endDate"
          class="form-input">
      </div>
    </div>
    
    <div class="form-group">
      <label class="checkbox-label">
        <input 
          type="checkbox" 
          name="isActive"
          [(ngModel)]="course.isActive"
          class="checkbox-input">
        Active Course
      </label>
    </div>
    
    <div class="form-actions">
      <button type="button" routerLink="/courses" class="cancel-btn">Cancel</button>
      <button type="submit" class="submit-btn" [disabled]="!isFormValid()">
        {{ isEditMode ? 'Update Course' : 'Add Course' }}
      </button>
    </div>
  </form>
</div>
